
public class LoopsWeek2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int a = 1; a <= 100; a++) {
			if (a % 15 == 0) {
				System.out.println("Hello World");
			}
			else if (a % 3 == 0) {
				System.out.println("Hello");
			}
			else if (a % 5 == 0) {
				System.out.println("World");
				
			} else {
			System.out.println(a);
		}
	
	}
			
	}
}		
	


